import React from 'react';


import Elemento from '../components/elemento';
import periodicTableData from '../periodicTableData.json';

import { getElementType, generateElectronDistribution } from '../util/elementosDefinitions'
import './tabelaPeriodica.css'


const [showModal, setShowModal] = useState(false);
const [electronDistribution, setElectronDistribution] = useState('');

const TabelaPeriodica = () => {

    const handleClick = () => {
        const distribution = generateElectronDistribution(atomicNumber);
        setElectronDistribution(distribution);
        setShowModal(true);
      };

  return (
    <div className="tabela-periodica">
      {periodicTableData.map((elemento, index) => (
    <div
        key={elemento.atomicNumber}
        className={`elemento ${getElementType(elemento)} ${index % 10 === 0 ? 'nova-linha' : ''}`}
      >
        <Elemento
          key={elemento.atomicNumber}
          atomicNumber={elemento.atomicNumber}
          symbol={elemento.symbol}
          name={elemento.name}
          atomicMass={elemento.atomicMass}
        />
        {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h2>Distribuição Atômica</h2>
            <p>{electronDistribution}</p>
            <button onClick={() => setShowModal(false)}>Fechar</button>
          </div>
        </div>
      )}
        </div>
      ))}
    </div>
  );
};

export default TabelaPeriodica;
