import React from 'react';
import './linusPaulingDiagram.css';
import { generateElectronDistribution, calculateLinusPaulingDiagram } from '../util/elementosDefinitions';


function LinusPaulingDiagram({ atomicNumber }) {
    const shells = calculateLinusPaulingDiagram(atomicNumber);

    return (
      <div className="diagram">
        {shells.map((electrons, index) => (
          <div key={index} className="layer">
            {renderShell(index)}
            <div className="sublevel">
              {renderSublevel('s', index)}
              {renderSublevel('p', index)}
              {renderSublevel('d', index)}
              {renderSublevel('f', index)}
            </div>
            {Array.from({ length: electrons - 2 }, (_, i) => (
              <div key={i} className="sublevel">
                {renderSublevels(i)}
              </div>
            ))}
            <p>{generateElectronDistribution(atomicNumber)}</p>
          </div>
        ))}
      </div>
    );
  }

  function renderShell(index) {
    const shells = ['K', 'L', 'M', 'N', 'O', 'P', 'Q'];
    if (shells[index]) {
      return <div className="shell">{shells[index]}</div>;
    }
    return null;
  }

  function renderSublevel(sublevel, index) {
    if ((sublevel === 's' && index === 0) || (sublevel === 'p' && index < 2)) {
      return <div className={sublevel} />;
    }
    return null;
  }

  function renderSublevels(index) {
    const sublevels = ['p', 'd', 'f'];
    return sublevels.map((sublevel) => {
      if (index < 2 && sublevel === 'p') {
        return <div key={sublevel} className={sublevel} />;
      }
      if (index < 4 && sublevel === 'd') {
        return <div key={sublevel} className={sublevel} />;
      }
      if (index < 6 && sublevel === 'f') {
        return <div key={sublevel} className={sublevel} />;
      }
      return null;
    });
  }

  export default LinusPaulingDiagram;
