import React from 'react';
import './linusPaulingDiagram.css';
import { generateElectronDistribution, calculateLinusPaulingDiagram } from '../util/elementosDefinitions';

function LinusPaulingDiagram({ atomicNumber }) {
  const shells = calculateLinusPaulingDiagram(atomicNumber);

  return (
    <div className="diagram">
      {shells.map((electrons, index) => (
        <div key={index} className="layer">
          {renderShell(index)}
          {renderSublevels(electrons)}
          <p>{generateElectronDistribution(atomicNumber)}</p>
        </div>
      ))}
    </div>
  );
}

function renderShell(index) {
  const shells = ['K', 'L', 'M', 'N', 'O', 'P', 'Q'];
  if (shells[index]) {
    return <div className="shell">{shells[index]}</div>;
  }
  return null;
}

function renderSublevels(electrons) {
  const sublevels = ['s', 'p', 'd', 'f'];

  return (
    <div className="sublevels">
      {sublevels.map((sublevel) => (
        <div key={sublevel} className={`${sublevel}-sublevel`}>
          {renderElectrons(sublevel, electrons)}
        </div>
      ))}
    </div>
  );
}

function renderElectrons(sublevel, electrons) {
  const maxElectrons = getMaxElectrons(sublevel);
  const electronsToRender = Math.min(maxElectrons, electrons);

  return Array.from({ length: electronsToRender }, (_, index) => (
    <div key={index} className="electron" />
  ));
}

function getMaxElectrons(sublevel) {
  switch (sublevel) {
    case 's':
      return 2;
    case 'p':
      return 6;
    case 'd':
      return 10;
    case 'f':
      return 14;
    default:
      return 0;
  }
}

export default LinusPaulingDiagram;
